import React, { Component } from 'react'
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSun } from '@fortawesome/free-solid-svg-icons'
import axios from 'axios'
import moment from 'moment'

export default class WeatherDay extends Component {
    // state = {
    //     currentWeather : '24',
    //     days : [
    //         {id:'1',day: 'Mon', min: '13', max:'34'},
    //         {id:'2',day: 'Tue', min: '13', max:'34'},
    //         {id:'3',day: 'Wed', min: '13', max:'34'},
    //         {id:'4',day: 'Thu', min: '13', max:'34'},
    //         {id:'5',day: 'Fri', min: '13', max:'34'},
    //     ]
    // }
    state = {
        current: {},
        days: []
    }
    componentDidMount(){
        axios.get('http://api.openweathermap.org/data/2.5/forecast?q=Boston&appid=57808ae0e0093dd410d6cf81f988f653&units=imperial')
            .then(res => {
                console.log(res)
                this.setState({
                    days: res.data.list
                })
            })

        axios.get('http://api.openweathermap.org/data/2.5/weather?q=Boston&appid=57808ae0e0093dd410d6cf81f988f653&units=imperial')
            .then(res => {
                console.log(res)
                this.setState({
                    current : {temp: res.data.main.temp, icon:res.data.weather[0].icon, wind:res.data.wind.speed, feels_like:res.data.main.feels_like}
                })
                console.log(this.state.current)
            })

    }

    render() {


        const {days} = this.state;
        console.log(days);
        var i=0;
        const dayList = [];
        var j=0;
        var tempMin = 100;
        var tempMax = -100;
        for(i;i<days.length;i++){
            if(days[i].main.temp_min<tempMin)
                tempMin = days[i].main.temp_min;
            if(days[i].main.temp_max>tempMax)
                tempMax = days[i].main.temp_max;
            if(i===6 || i===14 || i===22 || i===30 || i===38)
            {
                dayList[j]= {dt_txt: days[i].dt_txt, temp_min: tempMin, temp_max: tempMax, icon: days[i].weather[0].icon};
                j++;
                tempMin = 100;
                tempMax = -100;
            }          
        }
        console.log(dayList);
        // console.log(this.returnDay(new Date(days.list[0])));
        const dayInformation = dayList.length ? (
            dayList.map(day =>{
                return (
                    <div className="day" key={day.dt_txt}>
                       <h3>{moment(day.dt_txt).format('ddd')}</h3>
                            {/* <Link to={'/'+day.id}> */}
                                {/* <span className="card-title red-text">{day.name}</span> */}
                            {/* </Link> */}
                        <p>Low: {day.temp_min}&deg; F</p>
                        <p>High: {day.temp_max}&deg; F</p>
                        <img src={"http://openweathermap.org/img/wn/"+day.icon+"@2x.png"} />
                        {/* <p><FontAwesomeIcon icon={faSun} size="1x"/></p> */}

                    </div>
                )
            })
        ) : (
            <div className="center">No posts yet</div>
        );



        return (
            <div className="container">
                <div className="row">
                    <div className="col-md-4 col-md-offset-4">
                        <div className="weather">
                            <div className="current">
                                <div className="info">
                                    <div>&nbsp;</div>
                                    <div className="city">Boston</div>
                                    <div className="temp">{this.state.current.temp}&deg; <small>F</small><br/>
                                    <span className="feels"><small><small>Feels like:</small></small>{this.state.current.feels_like}&deg; <small>F</small></span>
                                    </div>
                                    <div className="wind"><small><small>Wind:</small></small> {this.state.current.wind} miles/hour</div>
                                    <div>&nbsp;</div>
                                </div>
                                <div className="icon">
                                    <p><FontAwesomeIcon icon={faSun} size="2x" color="yellow"/></p>
                                     {/* <img src={"http://openweathermap.org/img/wn/"+this.state.current.icon+"@2x.png"} /> */}
                                </div>
                            </div>
                            <div className="future">
                                {dayInformation}
                                {/* <div className="day">
                                    <h4>MONDAY</h4>
                                    <p><FontAwesomeIcon icon={faSun} size="2x"/></p>
                                    <div>
                                        <p>Low: 23&deg; F</p>
                                        <p>High: 77&deg; F</p>
                                    </div>
                                </div>
                                <div className="day">
                                    <h3>Tue</h3>
                                    <p><span className="wi-showers"></span></p>
                                </div>
                                <div className="day">
                                    <h3>Wed</h3>
                                    <p><span className="wi-rain"></span></p>
                                </div>
                                <div className="day">
                                    <h3>Mon</h3>
                                    <p><FontAwesomeIcon icon={faSun} size="2x"/></p>
                                </div>
                                <div className="day">
                                    <h3>Mon</h3>
                                    <p><FontAwesomeIcon icon={faSun} size="2x"/></p>
                                </div> */}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
